mapper.select:
select id, img, name, price, type_id as typeId
from goods


        type_id = #{typeId}

select id, name
from goods_type
where name = #{name}

select price
from goods
where id = #{id}

mapper.update:
update goods
set price=#{price}
where id = #{id}

mapper.select:
select id, img, name, description, price, type_id as typeId
from goods
where id = #{id}

select id, goods_id as goodsId, name, stock_num as stockNum, price
from goods_spec
where goods_id = #{id}

select price
from goods_spec
where id = #{specId}

select id, content, state, create_time as createTime, goods_id as goodsId, user_id as userId
from goods_msg
where state = 1;

mapper.update:
update goods_msg
set reply_content = #{replyContent},
    state         = 0,
    reply_time    = #{replyTime}
where id = #{id}
